       public function getCustomShippings(){
      $query = $this->db->query("SELECT * FROM custom_shipping ORDER BY zone_name ASC");
      return $query->rows;
  }
public function getCustomShipping($shipping_id){
  $query = $this->db->query("SELECT * FROM custom_shipping WHERE custom_shipping_id='" . (int)$shipping_id . "'");
  return $query->row;
}
  public function getEtd($product_id){
  $query = $this->db->query("SELECT * FROM  " . DB_PREFIX . "product WHERE product_id ='" . (int)$product_id . "'");
  if(!empty($query->row['etd'])){
  $etds = explode("-",$query->row['etd']);
  if (count($etds) > 1){
    $etd = $etds[1];
    }else{
    $etd = $query->row['etd'];
    }
  }else{
    $etd = 0;
  }
  return $etd;
}

